import { useState } from "react";
import Header from "../components/Header";
import RecommendChat from "../components/RecommendChat";
import styles from "../css/Recommend.module.css";
import sohee from "../img/sohee.jpg";

function Recommend(){
    const [messageContent, setMessageContent] = useState("");
    const [messages, setMessages] = useState("");
    const [recommendMovies, setRecommendMovies] = useState([]);

    // 메시지 입력값 변경 시 호출되는 함수
    const handleMessageContentChange = (event) => {
        setMessageContent(event.target.value);
    };

    // 전송 버튼 클릭 시 호출되는 함수
    const sendButtonClick = () => {
        // 입력한 메시지를 메시지 목록에 추가하고, 메시지 내용 초기화
        setMessages(messageContent);
        setMessageContent("");

        recommendMoviesApi();
    };


    const flow = {
        start: {
            message: "안녕하세요, FAQ 봇입니다!",
            options: ["예시", "깃허브", "디스코드"],
        },
        process_options: {
            message: (params) => {
              let link = "";
              switch (params.userInput) {
                case "예시":
                  link = "https://google.com";
                  break;
                case "깃헙":
                  link = "https://github.com/";
                  break;
                case "디스코드":
                  link = "https://naver.com";
                  break;
                default:
                  return "unknown_input";
              }
              setTimeout(() => {
                window.open(link);
              }, 1000);
              return `${params.userInput}로 보냈어요! 잠시 기다려 주세요!`;
            },
            transition: { duration: 1 },
            path: "loop",
        },
        loop: {
        message: "더 도움이 필요하신가요?",
        options: ["예시", "깃허브", "디스코드"],
        path: "process_options",
        },

    };

    return ( 
        <div>
            <Header />
            <div className={styles.chat}>
                <div className={styles.chat__message}>


                    //////여기에 info 내용을 넣고싶어


                </div>
                <div className={styles.chat__send}>
                    <div className={styles.input__container}>
                        <input 
                            type="text" 
                            className={styles.input__field} 
                            placeholder="메시지를 입력하세요..." 
                            value={messageContent}
                            onChange={handleMessageContentChange} 
                        />
                        <button onClick={sendButtonClick} className={styles.input__button}>전송</button>
                    </div>

                </div>
            </div> 
        </div>
    );
}

export default Recommend;
