import { useState } from "react";
import Header from "../components/Header";
import RecommendChat from "../components/RecommendChat";
import styles from "../css/Recommend.module.css";
import sohee from "../img/sohee.jpg";

function Recommend(){
    const [message, setMessage] = useState('');
    const [recommend, setRecommen] = useState([]);

    const handleMessageChange = (event) => {
        setMessage(event.target.value);
    };

    const handleSendMessage = () => {
        if (message.trim() !== '') {
            setRecommen([...recommend, message]);
        setMessage('');
        // 메시지 전송 후 추가적인 처리: 메시지를 입력한 사용자에 대한 메시지를 추가
        setRecommen(recommendContent => [
            ...recommendContent,
            `${message}을 입력하셨습니다`
        ]);
        }
    };

    return (
        <div>
        <div className="chat-window">
            {recommend.map((msg, index) => (
            <div key={index}>{msg}</div>
            ))}
        </div>
        <input
            type="text"
            value={message}
            onChange={handleMessageChange}
            placeholder="메시지를 입력하세요..."
        />
        <button onClick={handleSendMessage}>전송</button>
        </div>
    );
}

export default Recommend;
